using System;
using UnityEditor;
using UnityEngine;

namespace AlpacaIT.DynamicLighting.Editor {
    public class AboutWindowResources {
        private const string PAYPAL_LOGO_B64  = "";
        private const string KOFI_LOGO_B64    = "";
        private const string PATREON_LOGO_B64 = "";
        private const string DISCORD_LOGO_B64 = "";
        private const string GITHUB_LOGO_B64  = "";

        private static Texture2D _PaypalLogo;
        private static Texture2D _KoFiLogo;
        private static Texture2D _PatreonLogo;
        private static Texture2D _DiscordLogo;
        private static Texture2D _GitHubLogo;

        private static bool Linear {
            get => PlayerSettings.colorSpace == ColorSpace.Linear;
        }

        public static Texture2D PaypalLogo {
            get {
                _PaypalLogo ??= GenerateIcon( PAYPAL_LOGO_B64, 32, 39 );

                return _PaypalLogo;
            }
        }

        public static Texture2D KoFiLogo {
            get {
                _KoFiLogo ??= GenerateIcon( KOFI_LOGO_B64, 32, 22 );

                return _KoFiLogo;
            }
        }

        public static Texture2D PatreonLogo {
            get {
                _PatreonLogo ??= GenerateIcon( PATREON_LOGO_B64, 32, 35 );

                return _PatreonLogo;
            }
        }

        public static Texture2D DiscordLogo {
            get {
                _DiscordLogo ??= GenerateIcon( DISCORD_LOGO_B64, 32, 25 );

                return _DiscordLogo;
            }
        }

        public static Texture2D GitHubLogo {
            get {
                _GitHubLogo ??= GenerateIcon( GITHUB_LOGO_B64, 32, 32 );

                return _GitHubLogo;
            }
        }

        private static Texture2D GenerateIcon( string b64, int width, int height ) {
            Texture2D tex = new( width, height, TextureFormat.RGBA32, false, Linear );
            tex.LoadRawTextureData( Convert.FromBase64String( b64 ) );
            tex.Apply();

            return tex;
        }
    }
}
